import styles from './index.scss';
import 'swiper/swiper.scss';
import { Button, Image } from 'antd';

import { Navigation } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react/swiper-react';
import { useState } from 'react';
import tmp from '@/assets/ntf/pic/tanks2.png';
import { LeftOutlined, RightOutlined } from '@ant-design/icons';
interface objectT {
  [propName: string]: any;
}

const WalletList = (props: objectT) => {
  const { datas = {} } = props;
  const split_array = (arr: any, len: number) => {
    let a_len = arr.length;
    let result = [];
    for (let i = 0; i < a_len; i += len) {
      result.push(arr.slice(i, i + len));
    }
    return result;
  };
  const mapList = split_array(datas, 5);

  return (
    <div className={styles['wallet-swiper']}>
      {datas.length > 0 ? (
        <>
          <Swiper
            modules={[Navigation]}
            navigation={{
              prevEl: `.${styles['btn-prev']}`,
              nextEl: `.${styles['btn-next']}`,
            }}
          >
            {mapList.map((plist: any, index: number) => {
              return (
                <SwiperSlide key={index}>
                  <ul className={styles['items']}>
                    {plist.map((item: objectT) => {
                      return (
                        <li className={styles['item']} key={item.id}>
                          <div className={styles['msgs']}>
                            <div className={styles['title']}>Hatch</div>
                            <div className={styles['img']}>
                              <Image
                                preview={false}
                                src={tmp}
                                fallback="data:image/png;base64,"
                              />
                            </div>
                            <div className={styles['info']}>
                              <div className={styles['labels']}>
                                <label className={styles['label-1']}>
                                  #389532
                                </label>
                                <label className={styles['label-2']}>
                                  Level 2
                                </label>
                              </div>
                              <div className={styles['text']}>
                                <span className={styles['name']}>
                                  Rent(USDT/Day)Rent(USDT/Day)Rent(USDT/Day)Rent(USDT/Day)
                                </span>
                                <span className={styles['price']}>1000.00</span>
                              </div>
                            </div>
                          </div>

                          <div className={styles[`${item.type}`]}>
                            <Button type="primary">Listed</Button>
                          </div>
                        </li>
                      );
                    })}
                  </ul>
                </SwiperSlide>
              );
            })}
          </Swiper>
          {mapList.length > 1 ? (
            <div className={styles['swiper-ctrl']}>
              <span className={styles['btn-prev']}>
                <LeftOutlined />
              </span>
              <span className={styles['btn-next']}>
                <RightOutlined />
              </span>
            </div>
          ) : null}
        </>
      ) : (
        <div className={styles['empty']}></div>
      )}
    </div>
  );
};
export default WalletList;
